######################################################################################################################### KOP - 2022 / 2023 - FOCIS JÁTÉK# FÖLDES BENCE 'CINI' 4Kfrom ursina import *import randomfrom menu import *from player import *character1 = 'graphics/karakterek/focista_arsenal.png'character2 = 'graphics/karakterek/focista_chelsea.png'character3 = 'graphics/karakterek/focista_fradi.png'character4 = 'graphics/karakterek/focista_ujpest.png'ingame = 'graphics/menu/jatekban.png'app = Ursina()#menu = menu()#menu.open_menu()# ---------------------------------------------------------------------------------------------------------------------Ball = Entity(model='sphere', color=color.white, position = (0,2,-0.5), scale = (1.5,1.5,1.5))S1=0S2=0bx_direction = 0.1by_direction = 0.03def check_collide(E1, E2):    global bx_direction,by_direction    if E1.x < E2.x + E2.scale_x and E1.x + E1.scale_x > E2.x:        if E2.y < E1.y + E1.scale_y and E2.y + E2.scale_y > E1.y:            bx_direction = bx_direction * -1            if random.randint(1,2) == 2:                by_direction = by_direction * -1# ---------------------------------------------------------------------------------------------------------------------camera.fov = 10bgbg = Entity(rotation = (-90,0,0), model = 'plane', scale = (50.20, 0, 30.80), position = (0,4.4,-0.9), color = color.black)bg = Entity(rotation = (-90,0,0), model = 'plane', scale = (19.20, 0, 10.80), texture = ingame, position = (0,4.4,-1), double_sided = True)ground = Entity(model='cube', color=color.gray, scale=(20, 1, 1), collider='mesh', position = (0,10,0))top = Entity(model='cube', color=color.gray, scale=(20, 1, 1), collider='mesh', position = (0,0,0))left = Entity(model='cube', color=color.gray, scale=(1, 10, 1), collider='mesh', position = (-9.5,5,0))right = Entity(model='cube', color=color.gray, scale=(1, 10, 1), collider='mesh', position = (9.5,5,0))player1 = Player(scale_y=2, jump_height=2, jump_duration = .3, gravity = 0.8, position = (-5,2,0), control = ['space', 'd', 'd up', 'a', 'a up'], tex_ture = character1)player2 = Player(scale_y=2, jump_height=2, jump_duration = .3, gravity = 0.8, position = (5,2,0), control = ['up arrow', 'right arrow', 'right arrow up', 'left arrow', 'left arrow up'], tex_ture = character2)camera.position = [0,4.4,-110]#camera.add_script(SmoothFollow(target=player1, offset=[0,4,-300], speed=4))def update():    global bx_direction, by_direction,S1,S2    Ball.x += bx_direction    Ball.y += by_direction    check_collide(player1, Ball)    check_collide(player2, Ball)    if Ball.x >= 9.5:        bx_direction = bx_direction * -1    if Ball.x <= -9.5:        bx_direction = bx_direction * -1    if Ball.y >= 9:        by_direction = by_direction * -1    if Ball.y <= 1:        by_direction = by_direction * -1EditorCamera()app.run()