####################### MENUfrom ursina import *character1 = 'graphics/karakterek/focista_arsenal.png'character2 = 'graphics/karakterek/focista_chelsea.png'character3 = 'graphics/karakterek/focista_fradi.png'character4 = 'graphics/karakterek/focista_ujpest.png'menu_bg = 'graphics/menu/menu.png'ingame = 'graphics/menu/jatekban.png'choose = 'graphics/menu/valasztas.png'arrow = 'graphics/menu/nyil.png'# ----------------------------------------------------------------------------------------------------------------------list_characters = [character1, character2, character3, character4]bal = 2jobb = 1def nyilbalfel_click(self):    global bal    bal += 1    if bal > len(list_characters) - 1:        bal = 0    self.bal_karakter.texture = list_characters[bal]    self.bal_karakter.shake(duration=0.1, magnitude=1)def nyilballe_click(self):    global bal    bal -= 1    if bal < 0:        bal = len(list_characters) - 1    self.bal_karakter.texture = list_characters[bal]    self.bal_karakter.shake(duration=0.1, magnitude=1)def nyiljobbfel_click(self):    global jobb    jobb += 1    if jobb > len(list_characters) - 1:        jobb = 0    self.jobb_karakter.texture = list_characters[jobb]    self.jobb_karakter.shake(duration=0.1, magnitude=1)def nyiljobble_click(self):    global jobb    jobb -= 1    if jobb < 0:        jobb = len(list_characters) - 1    self.jobb_karakter.texture = list_characters[jobb]    self.jobb_karakter.shake(duration=0.1, magnitude=1)# ----------------------------------------------------------------------------------------------------------------------def jatek_click(self):   menu.close_menu(self)   menu.open_valasztas(self)def beallitasok_click(self):    passdef eredmenyek_click(self):    pass# ----------------------------------------------------------------------------------------------------------------------def onhover(self, type):    if type == 'menu':        self.color = color.white    if type == 'choose':        self.scale = (0.18, 0.18, 0)        self.color = color.dark_graydef onleave(self, type):    if type == 'menu':        self.color = color.black    if type == 'choose':        self.scale = (0.2, 0.2, 0)        self.color = color.red# ----------------------------------------------------------------------------------------------------------------------class menu:    def __init__(self):        self.canvas = Entity(texture = menu_bg, alpha=0, model='quad', scale=(1.920, 1.080, 0), position=[0, 0, 0], parent=camera.ui)        self.jatek_txt = Text(text='', position=[-0.73, 0.055, -0.01], color=color.black, parent=camera.ui,scale=[2, 1.8, 0])        self.beallitasok_txt = Text(text='', position=[-0.82, -0.11, -0.01], color=color.black,parent=camera.ui, scale=[2, 1.8, 0])        self.eredmenyek_txt = Text(text='', position=[-0.82, -0.29, -0.01], color=color.black,parent=camera.ui, scale=[2, 1.8, 0])        self.jatek_area = Entity(collider = 'box', alpha=0, model='quad', scale=(0.5, 0.13, 0), position=[-0.67, 0.042, -0.16],parent=camera.ui, on_click = Func(jatek_click, self), on_mouse_enter = Func(onhover, self.jatek_txt, 'menu'), on_mouse_exit = Func(onleave, self.jatek_txt, 'menu'))        self.beallitasok_area = Entity(collider = 'box', alpha=0, model='quad', scale=(0.5, 0.13, 0), position=[-0.67, -0.12, -0.1],parent=camera.ui, on_click = Func(beallitasok_click, self), on_mouse_enter = Func(onhover, self.beallitasok_txt, 'menu'), on_mouse_exit = Func(onleave, self.beallitasok_txt, 'menu'))        self.eredmenyek_area = Entity(collider = 'box', alpha=0, model='quad', scale=(0.5, 0.13, 0), position=[-0.67, -0.3, -0.1],parent=camera.ui, on_click = Func(eredmenyek_click, self), on_mouse_enter = Func(onhover, self.eredmenyek_txt, 'menu'), on_mouse_exit = Func(onleave, self.eredmenyek_txt, 'menu'))        self.jatek_area.disable()        self.beallitasok_area.disable()        self.eredmenyek_area.disable()        # ----------------------------------------        self.choose_bg = Entity(texture=choose, alpha=0, model='quad', scale=(1.920, 1, 0), position=[0, 0, 0], parent=camera.ui)        self.nyil_bf = Entity(texture = arrow, color = color.red, alpha=0, model='quad', scale=(0.2, 0.2, 0), position=[-0.5, 0.28,  -0.1], parent=camera.ui)        self.nyil_bl = Entity(double_sided = True,rotation_x = 180 , texture = arrow, color = color.red, alpha=0, model='quad', scale=(0.2, 0.2, 0), position=[-0.5, -0.28, -0.1], parent=camera.ui)        self.nyil_jf = Entity(texture = arrow, color = color.red, alpha=0, model='quad', scale=(0.2, 0.2, 0), position=[0.5, 0.28,   -0.1], parent=camera.ui)        self.nyil_jl = Entity(double_sided = True,rotation_x = 180 , texture = arrow, color = color.red, alpha=0, model='quad', scale=(0.2, 0.2, 0), position=[0.5, -0.28,  -0.1], parent=camera.ui)        self.nyil_bal_fel = Entity(collider = 'box', alpha = 0, model='quad', scale=(0.2, 0.2, 0), position=[-0.5, 0.28,  0], parent=camera.ui, on_click = Func(nyilbalfel_click, self), on_mouse_enter = Func(onhover, self.nyil_bf, 'choose'), on_mouse_exit = Func(onleave, self.nyil_bf, 'choose'))        self.nyil_bal_le  = Entity(collider = 'box', alpha = 0, model='quad', scale=(0.2, 0.2, 0), position=[-0.5, -0.28, 0], parent=camera.ui, on_click = Func(nyilballe_click, self), on_mouse_enter = Func(onhover, self.nyil_bl, 'choose'), on_mouse_exit = Func(onleave, self.nyil_bl, 'choose'))        self.nyil_jobb_fel= Entity(collider = 'box', alpha = 0, model='quad', scale=(0.2, 0.2, 0), position=[0.5, 0.28,   0], parent=camera.ui, on_click = Func(nyiljobbfel_click, self), on_mouse_enter = Func(onhover, self.nyil_jf, 'choose'), on_mouse_exit = Func(onleave, self.nyil_jf, 'choose'))        self.nyil_jobb_le = Entity(collider = 'box', alpha = 0, model='quad', scale=(0.2, 0.2, 0), position=[0.5, -0.28,  0], parent=camera.ui, on_click = Func(nyiljobble_click, self), on_mouse_enter = Func(onhover, self.nyil_jl, 'choose'), on_mouse_exit = Func(onleave, self.nyil_jl, 'choose'))        self.nyil_bal_fel.disable() ; self.nyil_bal_le.disable() ; self.nyil_jobb_fel.disable() ; self.nyil_jobb_le.disable()        self.bal_karakter =  Entity(texture = character1, alpha=0, model='quad', scale=(0.2, 0.2, 0), position=[-0.5, 0, -0.15], parent=camera.ui)        self.jobb_karakter = Entity(double_sided = True, rotation_y = 180 ,texture = character2, alpha=0, model='quad', scale=(0.2, 0.2, -0.15), position=[0.5, 0, 0], parent=camera.ui)    # ------------------------    def open_menu(self):        self.canvas.alpha = 1        self.jatek_txt.text = 'JÁTÉK'        self.beallitasok_txt.text = 'BEÁLLÍTÁSOK'        self.eredmenyek_txt.text = 'EREDMÉNYEK'        self.jatek_area.enable()        self.beallitasok_area.enable()        self.eredmenyek_area.enable()    def close_menu(self):        self.canvas.alpha = 0        self.jatek_txt.text = ''        self.beallitasok_txt.text = ''        self.eredmenyek_txt.text = ''        self.jatek_area.disable()        self.beallitasok_area.disable()        self.eredmenyek_area.disable()    # ------------------------    def open_valasztas(self):        self.choose_bg.alpha = 1        self.jobb_karakter.alpha = 1        self.bal_karakter.alpha = 1        self.nyil_bf.alpha = 1        self.nyil_bl.alpha = 1        self.nyil_jf.alpha = 1        self.nyil_jl.alpha = 1        self.nyil_bal_fel.enable() ; self.nyil_bal_le.enable() ; self.nyil_jobb_fel.enable() ; self.nyil_jobb_le.enable()    def close_valasztas(self):        self.choose_bg.alpha = 0        self.jobb_karakter.alpha = 0        self.bal_karakter.alpha = 0        self.nyil_bf.alpha = 0        self.nyil_bl.alpha = 0        self.nyil_jf.alpha = 0        self.nyil_jl.alpha = 0        self.nyil_bal_fel.disable() ; self.nyil_bal_le.disable() ; self.nyil_jobb_fel.disable() ; self.nyil_jobb_le.disable()