from ursina import *import randomapp = Ursina()P1 = Entity(model='quad', color=color.white, x=-6,y=-0,scale_y=2,scale_x=0.5)P2 = Entity(model='quad', color=color.white, x=6,y=0,scale_y=2,scale_x=0.5)Ball = Entity(model='sphere', color=color.white,z=0.1)S1=0S2=0S1T = Text(text=str(S1))S2T = Text(text=str(S2))bx_direction = 0.1by_direction = 0.03Ps = 3def check_collide(E1, E2):    global bx_direction,by_direction    if E1.x < E2.x + E2.scale_x and E1.x + E1.scale_x > E2.x:        if E2.y < E1.y + E1.scale_y and E2.y + E2.scale_y > E1.y:            bx_direction = bx_direction*-1            if random.randint(1,2) == 2:                by_direction = by_direction*-1def update():    global bx_direction,by_direction,S1,S2    P1.y += held_keys['w'] * time.dt * Ps    P1.y -= held_keys['s'] * time.dt * Ps    P2.y += held_keys['e'] * time.dt * Ps    P2.y -= held_keys['d'] * time.dt * Ps    Ball.x += bx_direction    Ball.y += by_direction    check_collide(P1,Ball)    check_collide(P2,Ball)    if Ball.y >= 3.5:        by_direction = by_direction*-1    elif Ball.y <= -3.5:        by_direction = by_direction*-1    if Ball.x >= 7:        Ball.x=0        Ball.y=0        S1+=1        S1T.text = str(S1)        print(str(S1))    elif Ball.x <= -7:        Ball.x=0        Ball.y=0        S2+=1        S2T.text = str(S2)        print(str(S2))app.run()